import login from "../../assets/loggin/login.jpg";
import adminLogin from "../../assets/loggin/adminLogin.jpg";
import { useLocation } from "react-router";

const location = useLocation().pathname

export const sectionStyle = {
  width: "100vw",
  height: "100vh",
  backgroundImage: `url("${location === '/login' ? login : adminLogin}")`,
  backgroundRepeat: "no-repeat",
  backgroundSize: "cover",
};
