import React from "react";
import "./styles.css";
import { Link, useLocation } from "react-router-dom";
import { Progress } from "antd";

const Card = ({
  id,
  img,
  title,
  button,
  icon,
  setState,
  stateIcon,
  url,
  bloque,
}) => {
  const location = useLocation().pathname;
  const completed = [];

  for (let i = 0; i < bloque?.unidades?.length; i++) {
    let status =
      localStorage.getItem(`check${bloque?.unidades[i]?.id}`) === "true"
        ? true
        : false;
    if (status === true) completed.push(status);
  }
  return (
    <div className="col-auto" key={id}>
      <div
        className="card position-relative"
        style={{ width: "20rem", borderRadius: "10px" }}
      >
        {icon && icon.length ? (
          <div className="position-absolute top-0 end-0">
            {stateIcon.key && stateIcon.key.length && stateIcon.key === id ? (
              <i className={stateIcon.style} onClick={() => setState(id)}></i>
            ) : (
              <i className={icon} onClick={() => setState(id)}></i>
            )}
          </div>
        ) : (
          ""
        )}

        {title && title.length ? (
          <div className="card-body">
            <h3 className="card-title text-center">{title}</h3>
          </div>
        ) : (
          ""
        )}

        {location === "/mis-bloques" ? (
          <div className="circle">
            <Progress
              type="circle"
              percent={Math.floor(
                (completed.length * 100) / bloque?.unidades.length
              )}
              format={() => `${completed.length}/${bloque?.unidades.length}`}
            />
          </div>
        ) : (
          <img src={img} />
        )}
        <Link to={url}>
          <div
            className={` ${button.styles} p-3 text-center`}
            style={{ width: "100%" }}
          >
            {button.text}
          </div>
        </Link>
      </div>
    </div>
  );
};

export default Card;
