import axios from 'axios'
import React, { useState } from 'react'
import { useEffect } from 'react'
import { useHistory } from 'react-router-dom';
import { useSelector } from 'react-redux';

export default function AdminBloques() {

    const [form, setForm] = useState({})
    const [roles, setRoles] = useState([])
    const user = useSelector((state) => state.user);
    const history = useHistory();

    useEffect(() => {
        axios.get('/api/roles')
        .then(res => res.data)
        .then(roles => roles.filter(rol => rol.id >= 3))
        .then(roles => setRoles(roles))
    }, [])

    const handleChange = (e) => {
        setForm({...form, [e.target.name]: e.target.value})
    }

    const handleMinimo = (e) => {
        console.log('ASDASA', e.target.checked)
    }


    if (user.rolId && user.rolId !== 1) {
      history.push("/unauthorized");
      return <><h1>No autorizado</h1></>;
    }

    return (
        <div>
            <h1>Crear nuevo bloque de formacion</h1>
            <form>
                <label htmlFor='titulo'>Titulo</label>
                    <input type='text' name='titulo' onChange={handleChange}></input>
                <label htmlFor='descripcion'>Descripcion</label>
                    <input type='text' name='descripcion' onChange={handleChange}></input>
                <p>Es bloque minimo?</p>
                    <label htmlFor='si'>Si</label>
                    <input type="radio" name='si' onChange={handleMinimo}/>
                    <label htmlFor='no'>No</label>
                    <input type="radio" name='minimo' onChange={handleMinimo}/>
                <p>A que roles esta destinado?</p>

                {roles.map(rol => {
                    return (
                        <div>
                        <input type="checkbox" name='roles'/>
                            <label htmlFor={rol.tipo} value={rol.id}>{rol.tipo}</label>
                        </div>
                    )
                })}

            </form>
        </div>
    )
}
