import axios from 'axios'
import React, { useState } from 'react'
import { useEffect } from 'react'
import { useHistory } from 'react-router-dom';
import { useSelector } from 'react-redux';

export default function AdminBloques() {

    const [form, setForm] = useState({})
    const [roles, setRoles] = useState([])
    const user = useSelector((state) => state.user);
    const history = useHistory();

    useEffect(() => {
        axios.get('/api/roles')
        .then(res => res.data)
        .then(roles => roles.filter(rol => rol.id >= 3))
        .then(roles => setRoles(roles))
    }, [])


    if (user.rolId && user.rolId !== 1) {
      history.push("/unauthorized");
      return <><h1>No autorizado</h1></>;
    }

    return (
        <div>
            <h1>Crear nuevo bloque de formacion</h1>
            <form>
                <label htmlFor='titulo'>Titulo</label>
                    <input type='text' name='titulo'></input>
                <label htmlFor='descripcion'>Descripcion</label>
                    <input type='text' name='descripcion'></input>
                <p>Es bloque minimo?</p>
                    <label htmlFor='si'>Si</label>
                    <input type="radio" name='minimo' value={true} />
                    <label htmlFor='no'>No</label>
                    <input type="radio" name='minimo' value={false} />
                <p>A que roles esta destinado?</p>
                {console.log(roles)}

            </form>
        </div>
    )
}
