import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { setUser } from "./redux/user";
import { Route, Switch, useHistory } from "react-router-dom";
import axios from "axios";
import Sidebar from "./components/SideBar/Sidebar";
import Navbar from "./components/Navbar/Navbar";
import Login from "./containers/Login/Login.jsx"
import Routes from './Routes.jsx'
import { useLocation } from "react-router";
import { getRoles } from "./redux/roles";
import { getSedes } from "./redux/sedes";
import { getBloques } from "../src/redux/bloques";
import { setUsers } from "./redux/users";
import "./App.css";

function App() {
  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory()

  useEffect(() => {
      dispatch(setUsers())
      .then(() => dispatch(getBloques()))
      .then(() => dispatch(getSedes()))
      .then(() => dispatch(getRoles()))
      .then(() => dispatch(getBloques()))

      axios.get("/api/users/me")
      .then((res) => res.data)
      .then((user) => axios.get(`/api/users/${user.id}`))
      .then((res) => res.data)
      .then((user) => dispatch(setUser(user)))
      .catch(err => {
        history.push('/login')
      })

  }, [dispatch, history, location.pathname]);

  return (
    <div className="App">
      {(location.pathname !== '/login' && location.pathname !== '/adminlogin') && <Sidebar />}
      {(location.pathname !== '/login' && location.pathname !== '/adminlogin') && <Navbar />}
        <Switch>
          <Route exact path="/login" component={Login} />
          <Route path="/adminlogin" component={Login} />
          <Route component={Routes} />
        </Switch>
    </div>
  );
}

export default App;
