import axios from 'axios'
import React, {useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getSedes } from '../../../redux/sedes'

export default function Sedes() {

    const [form, setForm] = useState({})

    const sedes = useSelector(state => state.sedes)
    const dispatch = useDispatch()

    const toggleForm = (id) => {
        console.log(document.getElementById(`sedeForm${id}`))
        // document.getElementById(`sedeForm${id}`).style.display = document.getElementById('sedeForm').style.display = 'block' ? 'none' : 'block'
    }

    const handleSubmit = (id) => {
        axios.put(`/api/sedes/${id}`, form)
        .then(res => res.data)
        .then(() => dispatch(getSedes()))
    }

    const handleChange = (e) => {
        setForm({name: e.target.value})
    }

    const handleDelete = (id) => {
        axios.delete(`/api/sedes/${id}`)
        .then(() => dispatch(getSedes()))
    } 

    return (
        <div>
            {sedes && sedes.map(sede => {
                return (
                    <div>
                        <h3>{sede.nombre}</h3>
                        <button onClick={() => toggleForm(`sedeForm${sede.id}`)} type='button'>Modificar sede</button>
                        <form onSubmit={() => handleSubmit(sede.id)} id={`sedeForm${sede.id}`} style={{display: 'none'}}>
                            <label htmlFor='nombre'>Nombre</label>
                            <input onChange={handleChange} type='text' name='nombre'></input>
                            <button type='submit'>Confirmar cambios</button>
                        </form>
                        <button onClick={handleDelete} type='button'>Borrar sede</button>
                    </div>
                )
            })}
        </div>
    )
}
