import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import axios from 'axios'
import { useHistory } from 'react-router'
import { Table } from 'antd'

export default function DashboardGestor() {

    const user = useSelector(state => state.user)
    const [voluntarios, setVoluntarios] = useState()
    const history = useHistory();

    useEffect(() => {
        axios.get(`/api/users/sede/${user.sedeId}`)
        .then(res => res.data)
        .then(voluntarios => setVoluntarios(voluntarios.filter(voluntario => voluntario.rolId > 2)))
        .catch(err => err)
    }, [user])

    const columns = [
        {
            title: 'Voluntario',
            dataIndex: 'voluntario',
            key: 'voluntario',
        },
        {
            title: 'Bloques minimos',
            dataIndex: 'bloquesMinimos',
            key: 'bloquesMinimos',
        },
        {
            title: 'Bloques opcionales',
            dataIndex: 'bloquesOpcionales',
            key: 'bloquesOpcionales',
        },
        {
            title: 'Entregas pendientes',
            dataIndex: 'entregasPendientes',
            key: 'EntregasPendientes',
        }
    ]

    const dataSource = voluntarios?.map(voluntario => {
        return (
          {
            key: voluntario.id,
            voluntario: voluntario.full_name,
            bloquesMinimos: entrega.bloque.titulo,
            estado: entrega.aprobado ? 'Completado' : 'Pendiente',
            fecha: date(entrega.updatedAt),
          }
        )
      })


    return (
        <div>
            <h1>Dashboard de {user.sede?.nombre}</h1>
            {console.log('VOLS', voluntarios)}
            {/* <Table columns={columns} dataSoure={dataSource} pagination={false} /> */}
        </div>
    )
}
