import { useHistory } from "react-router";

export default function authorize(user, permitted) {

    const history = useHistory()

    if (user.rolId === null || user.rolId !== permitted) {
        history.push("/unauthorized");
        return <><h1>No autorizado</h1></>;
      }
}
