import React, { useState, useEffect } from 'react'
import { useSelector } from "react-redux";
import "./style.css";
import CanchaFutbol from "./../../../assets/volunteer/CanchaFutbol.png";
import { useParams, useLocation } from "react-router";
import axios from "axios";
import { Link } from 'react'

const VolunteerModuls = () => {

  const [bloque, setBloque] = useState({})
  const [unidades, setUnidades] = useState([])
  const { id } = useParams();
  const location = useLocation().pathname

  useEffect(() => {
    axios.get(`/api/bloques/${id}`)
    .then(res => res.data)
    .then(bloque => setBloque(bloque))
    axios
      .get(`/api/unidades/${id}`)
      .then((res) => res.data)
      .then((data) => setUnidades(data))
  }, [])

  // const user = useSelector((state) => state.user);

  // const modulos = useSelector((state) => state.getUnidades);

  // console.log("modulos: ", modulos);

  return (
    <div>
      <div className="container-fluid">
        <div className="row">
          <h1 className="p-5 fs-1 title">
            <strong>{bloque.titulo}</strong>
          </h1>
        </div>
        <div className="row">
          <div className="col m-5 px-5 text-justify-2">
            <div className=" fs-4">
              <p>
                <strong>
                  Modulos
                </strong>
              </p>
            </div>
            <div className="d-flex flex-column mb-5">
              <button className="mb-3 mt-3 p-3 fs-3 button-style light-blue">
                Modulo 1
              </button>
              <button className="mb-3 mt-3 p-3 fs-3 button-style light-blue">
                Modulo 2
              </button>
              <button className="mb-3 mt-3 p-3 fs-3 button-style light-blue">
                Modulo 3
              </button>
            </div>
            <div className="fs-4 my-3">
              <p>
                <strong>Â¿Deseas integrarte al proyecto?</strong>
              </p>
              <Link to={`${location}/entrega`}>
              <button className="my-3 p-4 fs-3 button-style green">
                Entregar
              </button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default VolunteerModuls;
