import React from 'react'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { useParams } from "react-router";
import Checkbox from 'antd/lib/checkbox/Checkbox';

export default function Unidades() {

    const [unidades, setUnidades] = useState([])
    const { id } = useParams();
    const check = document.getElementById(`check${unidades[0]?.id}`)?.checked

    useEffect(() => {
        for (let i = 0; i < unidades.length; i++) {
            localStorage.getItem(`check${unidades[i].id}`) && 'true'
        }
        axios
      .get(`/api/unidades/bloque/${id}`)
      .then((res) => res.data)
      .then((data) => setUnidades(data))
    }, [check])

    const handleChange = (e) => {
        localStorage.setItem(e.target.id, 'checked');
    }

    return (
        <div>
            {console.log(check)}
            {unidades.map(unidad => {
                const id = `check${unidad.id}`
                return (
                <div>
                <a href={`${unidad.link}`} target='_blank'>
                    <button className="mb-3 mt-3 p-3 fs-3 button-style light-blue" id={`unidad${unidad.id}`}>
                    {unidad.titulo}
                    </button>
                </a>
                <Checkbox onChange={handleChange} id={id}>Completado</Checkbox>
                </div>
                )
            })}
        </div>
    )
}
