import React from "react";
import Sidebar from "../../../components/SideBar";
import { useSelector } from "react-redux";
import "./styles.css"
import { useEffect, useState } from "react";
import axios from "axios";


const VolunteerProfile = () => {

    const user = useSelector(state => state.user)

    return (
        <>
            <div>
                <div className="volunteer_profile_div">
                    <h2>Â¡Bienvenido, {user.full_name}!</h2>
                    {user.sede?.nombre && <h5>`Tu sede es ${user.sede?.nombre}`</h5>}
                    <p>{user.sede?.nombre ? user.sede?.nombre : 'Debes elegir una sede'}</p>
                    <h5>Rol</h5>
                    <p>{user.rolId ? user.rol.tipo : 'Por favor, pongase en contacto con su gestor para que se le asigne un rol'}</p>
                </div>
            </div>
        </>
    );
};

export default VolunteerProfile;
