          span: 8,
        }}
        initialValues={{
          remember: true,
        }}
        onValuesChange={(obj, obj2) => handleChange(obj, obj2)}
        onFinish={onFinish}
      >
        <Form.Item label="Tipo de rol" name="tipo">
          <Input />
        </Form.Item>

        <Button
          style={{ display: form.tipo ? "block" : "none" }}
          htmlType="submit"
          type="submit"
        >
          Crear
        </Button>
      </Form>



            <form onSubmit={handleSubmit} id='newRole' style={{display: 'none'}}>
                            <label htmlFor='tipo'>Tipo</label>
                            <input onChange={handleChange} type='text' name='tipo' ></input>
                            <button type='submit'>Crear</button>
                        </form>
            <Roles />
        </div>
    )
}
