import React, { useState } from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import axios from 'axios'
import { useHistory } from 'react-router'

export default function Roles() {
    const [form, setForm] = useState({})

    const roles = useSelector(state => state.roles)
    const dispatch = useDispatch()
    const history = useHistory();
    const user = useSelector(state => state.user)

    const toggleForm = (id) => {
        console.log(document.getElementById(id))
        document.getElementById(id).style.display = document.getElementById(id).style.display === 'block' ? 'none' : 'block'
    }

    const handleSubmit = (id) => {
        axios.post(`/api/roles/`, form)
        .then(res => res.data)
        .then(() => dispatch(getRoles()))
    }

    const handleChange = (e) => {
        setForm({nombre: e.target.value})
    }

    const handleDelete = (id) => {
        axios.delete(`/api/roles/${id}`)
        .then(() => dispatch(getRoles()))
    }

    if (user.rolId && user.rolId !== 1) {
        history.push("/unauthorized");
        return <><h1>No autorizado</h1></>;
      }

    return (
        <div>
            {roles && roles.map(rol => {
                return (
                    <div>
                        <h3>{rol.nombre}</h3>
                        <button onClick={() => toggleForm(`sedeForm${rol.id}`)} type='button'>Modificar sede</button>
                        <form onSubmit={() => handleSubmit(rol.id)} id={`sedeForm${rol.id}`} style={{display: 'none'}}>
                            <label htmlFor='nombre'>Tipo</label>
                            <input onChange={handleChange} type='text' name='nombre' placeholder={rol.tipo}></input>
                            <button type='submit'>Confirmar cambios</button>
                        </form>
                        <button onClick={() => handleDelete(rol.id)} type='button'>Borrar rol</button>
                    </div>
                )
            })}
        </div>
    )
}
