import React from "react";
import Sidebar from "../../../components/SideBar";
import { useSelector } from "react-redux";
import "./styles.css"
import { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";


const VolunteerProfile = () => {

    const user = useSelector(state => state.user)
    const history = useHistory();

    const handleClick = () => {
        history.push('/sede')
    }

    return (
        <>
            <div>
                <div className="volunteer_profile_div">
                    <h2>Â¡Bienvenido, {user.full_name}!</h2>
                    {user.sede?.nombre ? <h5>Tu sede es: {user.sede?.nombre}</h5> : <h5 onClick={handleClick}>Debes elegir una sede</h5>}
                    {user.rol?.tipo ? <h5>Tu rol es: {user.rol?.tipo}</h5> : <h5>Ponete en contacto con tu gestor para que te asigne un rol</h5>}
                </div>
            </div>
        </>
    );
};

export default VolunteerProfile;
