import axios from "axios";
import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Swal from "sweetalert2";
import { getRoles } from "../../../redux/sedes";
import { Table, Button, message} from "antd";
import useAuthorize from "../../../utils/authorization";

export default function Sedes() {
  const [form, setForm] = useState({});

  const roles = useSelector((state) => state.roles);
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);

  const toggleForm = (id) => {
    document.getElementById(id).style.display =
      document.getElementById(id).style.display === "block" ? "none" : "block";
  };

  const handleSubmit = (e, id) => {
      e.preventDefault()
    axios
      .post(`/api/roles/${id}`, form)
      .then((res) => res.data)
      .then(() => dispatch(getRoles()));
  };

  const handleChange = (e) => {
    setForm({...form, [e.target.name]: e.target.value });
  };

  const alertaEliminar = Swal.mixin({
    buttonsStyling: true,
  });

  const handleDelete = (id) => {
    alertaEliminar
      .fire({
        title: "Estás seguro?",
        text: "Si lo confirmas, no podrás deshacerlo!",
        icon: "warning",
        showCancelButton: true,
        confirmButtonText: "Si, borrar!",
        cancelButtonText: "No, cancelar!",
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        reverseButtons: true,
      })
      .then((result) => {
        if (result.isConfirmed) {
          alertaEliminar.fire(
            "Eliminado!",
            "El bloque fue eliminado correctamente.",
            "success"
          );
          axios.delete(`/api/roles/${id}`).then(() => dispatch(getRoles()));
          message.success("Sede eliminada correctamente")
        } else if (result.dismiss === Swal.DismissReason.cancel) {
          alertaEliminar.fire("Cancelado", "El bloque está a salvo", "error");
        }
      });
  };





  useAuthorize(user, 1)

  const dataSource = roles.map((rol) => {
    return {
      key: rol.id,
      rol: rol.tipo,
    };
  });

  const columns = [
    {
      title: "Rol",
      dataIndex: "tipo",
      key: "tipo",
    },
    {
      title: "Modificar/eliminar",
      key: "modificar/eliminar",
      render: (text, record) => {
          return(
        <div>
          <Button
            onClick={() => toggleForm(`sedeForm${record.key}`)}
            type="button"
          >
            Modificar sede
          </Button>
          <form onSubmit={(e) => handleSubmit(e, record.key)} id={`sedeForm${record.key}`} style={{display: 'none'}}>
                            <label htmlFor='nombre'>Nombre</label>
                            <input onChange={handleChange} type='text' name='nombre' placeholder={record.nombre}></input>
                            <label htmlFor='comunidadId'>Comunidad ID</label>
                            <input onChange={handleChange} type='number' name='comunidadId' placeholder={record.comunidadId}></input>
                            <button type='submit'>Confirmar cambios</button>
                        </form>
          <Button onClick={() => handleDelete(record.key)} type='button'>Eliminar sede</Button>
        </div>
          )
      },

    },
  ];

  return (
    <div>
      <Table bordered dataSource={dataSource} columns={columns} pagination={false} />
    </div>
  );
}
